public class NetEaseAnnounce implements Article {

    public final String rootTag = "businessnews";
    public final String elemTag = "";

    public String title;    //
    public String url;  //url
    public String time; //Ϣ
    public String type; //ؼ
    public String[] types;    //ؼ
    public String text; //

    public String getRootTag() {
        return rootTag;
    }
    public String getUrl() {
        return url;
    }

    public void setUrl(String url) {
        this.url = url;
    }

    public String getTitle() {
        return title;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public String getTime() {
        return time;
    }

    public void setTime(String time) {
        this.time = time;
    }

    public String getType() {
        return type;
    }

    public void setType(String type) {
        this.type = type;
    }

    public String getText() {
        return text;
    }

    public void setText(String text) {
        this.text = text;
    }


    public String getContent() {
        return getText();
    }

    /**
     * һƪ
     * @param url
     * @param title
     * @param time
     * @param type
     * @param text
     */
    public NetEaseAnnounce(String url, String title, String time, String type, String text) {
        this.url = url;
        this.title = title;
        this.time = time;
        this.type = type;
        this.text = text;

        types = Sentenizer.split3(type);
    }

    public NetEaseAnnounce(Element elem) {
        this.url = elem.elementText("url");
        this.title = elem.elementText("title");
        this.time = elem.elementText("time");
        this.type = elem.elementText("type");
        this.text = elem.elementText("text");

        types = Sentenizer.split3(type);
    }


    public Element createElement() {
        Element root = DocumentHelper.createElement(elemTag);

        Element urlElem = root.addElement("url");
        urlElem.setText(this.getUrl());

        Element titleElem = root.addElement("title");
        titleElem.setText(this.getTitle());

        Element timeElem = root.addElement("time");
        timeElem.setText(this.getTime());

        Element typeElem = root.addElement("type");
        typeElem.setText(this.getType());

        Element textElem = root.addElement("text");
        textElem.setText(this.getText());
        return root;
    }
}
